import { Component } from "react";
import "./card.styles.css";

class Card extends Component {
  constructor() {
    super();
    this.state = {
      showImage:"https://upload.wikimedia.org/wikipedia/commons/thumb/0/0c/Vector_Loading.svg/1200px-Vector_Loading.svg.png"
    };
  }


  changeDivByTitle = (props) => {
    const { title, id, name, ip, machineType, wantedWidth, showImage } =
    this.props;
    if (machineType.includes('Windows')) {
      this.setState(()=>{
        this.state.showImage = 'https://cdn-icons-png.flaticon.com/512/1304/1304089.png'
      })
    }
    return (
      <div className="card-container" key={id}>
        <img
          className="imgInDiv"
          alt={`host ${name}`}
          src={this.state.showImage}
          width={wantedWidth}
        />
        <div className="host-info">
          <h2 className="header-h2">{name}</h2>
          <div>
            <p>IP - {ip}</p>
            <p>Machine Type - {machineType}</p>
          </div>
        </div>
      </div>
    );
  };

  render() {
    // document.body.style.overflow = "hidden"
    const { title, id, name, ip, machineType, wantedWidth, showImage } =
      this.props;

    return (
        <div>
          {this.changeDivByTitle(title, ip, machineType)}
        </div>

    );
  }
}

export default Card;
