import { Component } from "react";
import "./card.styles.css";

class Card extends Component {
  constructor() {
    super();
    this.state = {
      showImageState: "https://upload.wikimedia.org/wikipedia/commons/thumb/0/0c/Vector_Loading.svg/1200px-Vector_Loading.svg.png",
      idState:0,
      nameState: "Loading...",
      emailState: "Please Wait...",
      wantedWidthState: "70",
    };
  }
  render() {
    // document.body.style.overflow = "hidden"
    const { title, id, name, email, wantedWidth, showImage } = this.props;
    if (title.includes('errorNotFound')){
      this.setState(()=>{
        return(

          {
            showImageState: "https://upload.wikimedia.org/wikipedia/commons/thumb/0/0c/Vector_Loading.svg/1200px-Vector_Loading.svg.png",
            idState:0,
            nameState: "Loading...",
            emailState: "Please Wait...",
            wantedWidthState: "70",
          }
        )

      })
      // errorNotFound()
    }
    return (
      <div className="card-container" key={id}>
        <img
          className="imgInDiv"
          alt={`host ${name}`}
          src={showImage}
          width={wantedWidth}
        />
        <div className="host-info">
          <h2 className="header-h2">{name}</h2>
          <p>{email}</p>
        </div>
      </div>
    );
  }
}

export default Card;
