import { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      linuxHosts: [],
      searchField:''
    };

  }

  onSearchChange = (event) => {
    const searchField = event.target.value.toLocaleLowerCase();

      this.setState(() => {
        return { searchField };

    })
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) =>
        this.setState(() => {
          return { linuxHosts: users };
        })
      );
  }

  render() {

    const { linuxHosts,searchField } = this.state;
    const { onSearchChange } = this;

    const filterItems = this.state.linuxHosts.filter((item) => {
      return item.name.toLocaleLowerCase().includes(this.state.searchField);
    });

    return (
      <div className="App">
        <input
          className="search-box"
          type="search"
          placeholder="Searching ..."
          onChange={this.onSearchChange}
        />
        {filterItems.map((linuxHosts) => {
          return (
            <div key={linuxHosts.id}>
              <h1>{linuxHosts.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
