import { Component } from "react";
import Card from "../card/card.component";
import "./card-list.styles.css";

class CardList extends Component {
  constructor() {
    super();
    this.state = {
      searchingOnce: true,
    };
  }
  render() {
    const { linuxHosts } = this.props;

    if (linuxHosts.length == 0) {
      if (this.state.searchingOnce){
        this.state.searchingOnce = false
        return <Card id={0} name={'Loading...'} email={'Please Wait...'} wantedWidth="70"  showImage='https://upload.wikimedia.org/wikipedia/commons/thumb/0/0c/Vector_Loading.svg/1200px-Vector_Loading.svg.png' />;
      }else{
        return <Card id={0} name={'Cannot Find Items'} email={'Cannot find items '} wantedWidth="70" showImage='https://cdn-icons-png.flaticon.com/512/1304/1304089.png'/>;

      }

    }
    return (
      <div className="card-list">
        {linuxHosts.map((eachHost) => {
          const { name, email, id } = eachHost;
          return <Card id={id} name={name} email={email} wantedWidth="70" showImage='https://cdn-icons-png.flaticon.com/512/915/915974.png?w=360' />;
        })}
      </div>
    );
  }
}

export default CardList;
