import {Component} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor() {
    super();
    this.state = {
      fname:'Bar',
      lname:'haymovich',
      company:'intel aka ness'
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
           Hi {this.state.fname} {this.state.lname}, I work at {this.state.company}
          </p>
          <button onClick={() => {
            this.setState({fname:'Matan',lname:'haymovich',company:'Amarel'})
          }}>Change the name </button>
        </header>
      </div>
    );

  }
}

export default App;
