import { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      linuxHosts: [],
    };
    this.origianlData = [];
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) =>
        this.setState(() => {
          this.origianlData = { linuxHosts: users };
          return { linuxHosts: users };
        })
      );
  }

  render() {
    return (
      <div className="App">
        <input
          className="search-box"
          type="search"
          placeholder="Searching ..."
          onChange={(event) => {
            console.log(this.origianlData);
            let _getValue = event.target.value.toLocaleLowerCase();
            const filterItems = this.state.linuxHosts.filter((item) => {
              if (Boolean(_getValue)) {
                return item.name.toLocaleLowerCase().includes(_getValue);
              } else {
                return this.origianlData
              }
            });
            this.setState(() => {
              return { linuxHosts: filterItems };
            });
          }}
        />
        {this.state.linuxHosts.map((linuxHosts) => {
          return (
            <div key={linuxHosts.id}>
              <h1>{linuxHosts.name}</h1>
            </div>
          );
        })}
      </div>
    );
  }
}

export default App;
