import {Component} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor() {
    super();
    this.state = {
      linuxHosts : []
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((response) => response.json())
      .then((users) => this.setState(()=>{
        return {linuxHosts:users}
      }))
  }

  render() {
    return (
      <div className="App">
        {
          this.state.linuxHosts.map((linuxHosts) => {
            return (
            <div key={linuxHosts.id}>
              <h1>{linuxHosts.name} - {linuxHosts.ip}</h1>
            </div>
            )})
        }
      </div>
    );

  }
}

export default App;
