import { Component } from "react";
import "./card.styles.css";

class Card extends Component {
  changeDivByTitle = (titleToCheck, ip = "None", machineType = "None") => {
    return (

      <div>
        <img
          className="imgInDiv"
          alt={`host ${this.props.name}`}
          src={this.props.showImage}
          width={this.props.wantedWidth}
        />
        <p>IP - {ip}</p>
        <p>Machine Type - {machineType}</p>
      </div>
    );
  };

  render() {
    // document.body.style.overflow = "hidden"
    const { title, id, name, ip, machineType, wantedWidth, showImage } =
      this.props;

    return (
      <div className="card-container" key={id}>
        <img
          className="imgInDiv"
          alt={`host ${name}`}
          src={showImage}
          width={wantedWidth}
        />
        <div className="host-info">
          <h2 className="header-h2">{name}</h2>
          {this.changeDivByTitle(title, ip, machineType)}
        </div>
      </div>
    );
  }
}

export default Card;
